mapper.select:
SELECT nb_num, nb_title, nb_price, nb_writer, nb_publisher, TO_CHAR(nb_publi_date, 'YYYY-MM-DD') as nb_publi_date, nb_category1, nb_category2, nb_image, r_rating FROM ( SELECT rownum rn, nb.*, re.r_rating FROM newbook nb, review re WHERE nb.nb_num = re.nb_num AND nb.nb_category1 = 1
ORDER BY nb.nb_publi_date desc
ORDER BY re.r_rating desc
ORDER BY nb.nb_price desc
ORDER BY nb.nb_price asc
) WHERE rn BETWEEN #{start} AND #{end}
