mapper.select:
		SELECT *
		FROM (SELECT rownum rn, a.*
			  FROM (SELECT o.*, m.m_name, order_nb_title(o.o_order_num) nb_title
                    FROM   orderr o, member m
                    WHERE  o.m_num = m.m_num(+)
                    ORDER BY o.o_order_date DESC
                    ) a
			  )
		WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE orderr
SET    o_status = 5
WHERE  o_order_num = #{o_order_num}

UPDATE orderr
SET    o_status = 3
WHERE  o_order_num = #{o_order_num}

UPDATE orderr
SET    o_status = 4
WHERE  o_order_num = #{o_order_num}

UPDATE orderr
SET	   o_status = 2, o_deliv_com = #{o_deliv_com}, o_deliv_track = #{o_deliv_track}
WHERE  o_order_num = #{o_order_num}

UPDATE orderr
SET    o_status = 6
WHERE  o_order_num = #{o_order_num}

UPDATE orderr
SET    o_status = 7
WHERE  o_order_num = #{o_order_num}

mapper.select:
SELECT nb.nb_title, nb.nb_price, od.o_de_count, od.o_de_prodtype FROM newbook nb, order_detail od WHERE nb.nb_num = od.nb_num AND od.o_order_num = #{o_order_num}
UNION
SELECT nb.nb_title, nb.nb_price, od.o_de_count, od.o_de_prodtype FROM newbook nb, order_detail od WHERE nb.nb_num = (SELECT ob.nb_num FROM oldbook ob WHERE od.nb_num = ob.ob_num) AND od.o_order_num = #{o_order_num}

mapper.update:
 	UPDATE member
	SET m_point = m_point + (#{o_pay_price} * 0.01)
	WHERE m_num = #{m_num}

 	UPDATE orderr
	SET    o_status = 1, o_rec_name = #{o_rec_name}, o_rec_mail = #{o_rec_mail}
		   , o_rec_ph = #{o_rec_ph}, o_rec_addr = #{o_rec_addr}, o_rec_msg = #{o_rec_msg}
	WHERE  o_order_num = #{o_order_num}

 	UPDATE order_gift
	SET    o_gift_name = #{o_gift_name}, o_gift_ph = #{o_gift_ph}, o_gift_accept = 1
	WHERE  o_order_num = #{o_order_num}
