mapper.select:
Select sum(m_point) as point 
From point_list 
Where m_num=#{m_num}

mapper.update:
Update member
Set    m_point = m_point +#{m_point}
Where  m_num = #{m_num}

Update member
Set    m_point = m_point-#{m_point}
Where  m_num = #{m_num}

mapper.select:
Select count(*) 
From 
(
	Select count(*) 
	From point_list 
	Where a_num =#{eNum} 
	Or q_num=#{eNum}
	Group by m_num
)
