mapper.select:
SELECT * FROM ( SELECT rownum rn , a.nb_title, a.nb_price , a.m_num, a.m_name , a.m_id , a.nb_writer, a.nb_publisher , a.nb_image , TO_CHAR(a.nb_publi_date, 'YYYY-MM-DD')AS nb_publi_date, a.ob_num, a.ob_acc_name, a.ob_acc_num, a.nb_summary , a.nb_isbn , a.nb_page , a.nb_size, a.nb_category1 , a.nb_category2 , a.ob_grade, a.ob_pur_price, a.ob_status, a.ob_sell_price, TO_CHAR(a.ob_report_date, 'YYYY-MM-DD') AS ob_report_date, a.ob_trans_com, a.ob_trans_num, a.ob_ripped, a.ob_scribble, a.ob_smeary, a.ob_write_date, a.ob_readcnt FROM
(select n.nb_title, n.nb_price , m.m_num as m_num , o.m_num as om_num , m.m_id as m_id , m.m_name as m_name , n.nb_writer, n.nb_publisher , n.nb_image , n.nb_publi_date, o.ob_num, o.ob_acc_name, o.ob_acc_num, n.nb_summary , n.nb_isbn , n.nb_page , n.nb_size, n.nb_category1 , n.nb_category2 , o.ob_grade, o.ob_pur_price, o.ob_status, o.ob_sell_price, o.ob_report_date, o.ob_trans_com,o.ob_trans_num , o.ob_ripped, o.ob_scribble, o.ob_smeary, o.ob_write_date, o.ob_readcnt from newbook n , oldbook o , member m WHERE o.nb_num = n.nb_num and m.m_num= o.m_num order by o.ob_num) a
WHERE ob_status IN 3 ) WHERE rn >= #{start} and rn <= #{end}

		
		SELECT * 
		FROM
		(
			SELECT rownum rn,  a.*
			from (
			 SELECT  nb_num, nb_title, nb_price, nb_writer,
			nb_publisher , nb_image, TO_CHAR(nb_publi_date, 'YYYY-MM-DD') AS nb_publi_date
			From newbook) a 
			) 
		WHERE rn >= #{start}
		and   rn <= #{end}

mapper.update:
UPDATE oldbook 
SET 
	ob_status = #{ob_status}
	WHERE ob_num =#{ob_num}
