mapper.update:
update member SET 
	m_image = #{m_image} ,
	m_email = #{m_email} , 
	m_ph = #{m_ph}, 
	m_addr = #{m_addr}
 where m_num= #{m_num}

mapper.select:

select * from 
(	
	select rownum rn , m.* 
	from 
		(select * from member order by m_num desc) m
)
where rn >= #{start}
and rn <= #{end}


select *
from (
  select rownum rn, m.*
  from (
    select  m.m_num , m.m_id,  m.m_name,  
            m.m_email, m.m_ph, m.m_admin ,

            mq.mq_num,  mq.mq_title, mq.mq_content, 
            mq.mq_date, mq.mq_hidden

    from member m
    join memberq mq
    on m.m_num = mq.m_num
    order by mq.mq_date desc
  ) m
)
	where rn >= #{start}
	and rn <= #{end}

mapper.update:

update member set
	 m_id = #{m_id} , m_name = #{m_name},
    m_email = #{m_email}, m_ph = #{m_ph}, 
    m_addr = #{m_addr}, m_birth = #{m_birth}, m_pw = #{m_pw},
    m_admin = #{m_admin} ,m_wd = #{m_wd}, m_image=#{m_image}
where m_num = #{m_num}

mapper.select:
select * from (
select rownum rn , m.*
from (select * from member order by m_num desc) m )
where rn BETWEEN #{start} and #{end}
and ( m_num like '%' || #{keyword} || '%' or m_id like '%' || #{keyword} || '%' or m_name like '%' || #{keyword} || '%' or m_addr like '%' || #{keyword} || '%' or m_email like '%' || #{keyword} || '%' )
